function getUserinfo(){$.ajax({type:"GET",url:"/my/userinfo",headers:{Authorization:localStorage.getItem("token")||""},success:function(res){0!==res.status&&layui.layer.msg("获取用户信息失败"),renderAvatar(res.data)}})}function renderAvatar(usr){let name=usr.nickname||usr.username;if($("#welcome").html("欢迎 "+name),null!==usr.user_pic)$(".layui-nav-img").attr("src",usr.user_pic).show(),$(".text-avatar").hide();else{$(".layui-nav-img").hide();let cap=name.charAt(0).toUpperCase();$(".text-avatar").html(cap).show}}$((function(){getUserinfo()}));